import React, { useState } from "react";

const UploadPage = () => {
    const [selectedFile, setSelectedFile] = useState(null);
    const [message, setMessage] = useState("");
    const [sessionId, setSessionId] = useState("");
    const [toc, setToc] = useState(null);
    const [userQuery, setUserQuery] = useState("");
    const [answer, setAnswer] = useState("");

    const handleFileUpload = async (e) => {
        e.preventDefault();

        if (!selectedFile) {
            setMessage("Please select a PDF file to upload.");
            return;
        }

        const formData = new FormData();
        formData.append("file", selectedFile);

        try {
            const response = await fetch("http://localhost:8000/upload", {
                method: "POST",
                body: formData,
            });

            const data = await response.json();
            console.log("Upload response:", data);
            setMessage("‚úÖ Upload successful!");
            setSessionId(data.session_id);
            setToc(data.table_of_contents);
        } catch (error) {
            console.error("Upload failed:", error);
            setMessage("‚ùå Upload failed. Check console for details.");
        }
    };

    const handleAskQuery = async () => {
        if (!userQuery.trim()) return;

        const formData = new FormData();
        formData.append("session_id", sessionId);
        formData.append("query", userQuery);

        try {
            const response = await fetch("http://localhost:8000/query", {
                method: "POST",
                body: formData,
            });

            const data = await response.json();
            setAnswer(data.response || "No answer returned.");
        } catch (err) {
            console.error("Query failed:", err);
            setAnswer("‚ùå Failed to get answer.");
        }
    };

    return (
        <div style={{ padding: "2rem", fontFamily: "Arial" }}>
            <h2>Upload a PDF Document</h2>
            <form onSubmit={handleFileUpload}>
                <input
                    type="file"
                    accept="application/pdf"
                    onChange={(e) => setSelectedFile(e.target.files[0])}
                />
                <br /><br />
                <button type="submit">Upload</button>
            </form>

            {message && <p>{message}</p>}

            {toc && (
                <div style={{ marginTop: "2rem" }}>
                    <h3>üìÑ Table of Contents</h3>
                    <pre style={{ background: "#f9f9f9", padding: "1rem", borderRadius: "8px" }}>
                        {JSON.stringify(toc, null, 2)}
                    </pre>

                    <h3>üí¨ Ask a Question</h3>
                    <input
                        type="text"
                        placeholder="Ask something about the document..."
                        value={userQuery}
                        onChange={(e) => setUserQuery(e.target.value)}
                        style={{ width: "60%", padding: "0.5rem", marginRight: "0.5rem" }}
                    />
                    <button onClick={handleAskQuery}>Ask</button>

                    {answer && (
                        <div style={{ marginTop: "1rem", background: "#eef", padding: "1rem" }}>
                            <strong>Answer:</strong>
                            <p>{answer}</p>
                        </div>
                    )}
                </div>
            )}
        </div>
    );
};

export default UploadPage;
