import React, { useState } from "react";
import "./ChatInterface.css";
import logo from "../assets/logo.JPG";

const ChatInterface = () => {
  const [file, setFile] = useState(null);
  const [toc, setToc] = useState(null);
  const [sessionId, setSessionId] = useState(null);
  const [query, setQuery] = useState("");
  const [responses, setResponses] = useState([]);
  const [loading, setLoading] = useState(false);
  const [tocExpanded, setTocExpanded] = useState({});

  const handleUpload = async () => {
    if (!file) return;
    const formData = new FormData();
    formData.append("file", file);
    setLoading(true);

    try {
      const res = await fetch("http://localhost:8000/upload", {
        method: "POST",
        body: formData,
      });

      const data = await res.json();
      setToc(data.table_of_contents);
      setSessionId(data.session_id);
    } catch (err) {
      console.error("Upload failed:", err);
    } finally {
      setLoading(false);
    }
  };

  const handleQuery = async () => {
    if (!query.trim() || !sessionId) return;
    const formData = new FormData();
    formData.append("session_id", sessionId);
    formData.append("query", query);

    try {
      const res = await fetch("http://localhost:8000/query", {
        method: "POST",
        body: formData,
      });

      const data = await res.json();
      setResponses((prev) => [...prev, { query, response: data.response }]);
      setQuery("");
    } catch (err) {
      console.error("Query failed:", err);
    }
  };

  const toggleTocItem = (index) => {
    setTocExpanded((prev) => ({ ...prev, [index]: !prev[index] }));
  };

  return (
    <div className="chat-container">
      <div className="sidebar">
        <img src={logo} alt="Logo" className="logo" />
        <input
          type="file"
          onChange={(e) => setFile(e.target.files[0])}
          style={{ marginBottom: "1rem" }}
        />
        <button onClick={handleUpload}>Upload</button>

        {loading && (
          <div className="loading">
            <p>ðŸ“„ Loading... extracting document data and building index, please wait.</p>
          </div>
        )}

        {toc && !loading && (
          <div className="toc">
            <h3>Table of Contents</h3>
            <ul>
              {toc.map((item, index) => (
                <li key={index}>
                  <div onClick={() => toggleTocItem(index)} className="toc-item">
                    â–¶ {item}
                  </div>
                  {tocExpanded[index] && (
                    <div className="toc-details">More details for {item}...</div>
                  )}
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>

      <div className="main-content">
        {!toc && !loading ? (
          <div className="welcome-message">
            <h2>ðŸ‘‹ Welcome! I'm your AI-powered RFQ Assistant.</h2>
            <p>
              I can help you to:
              <br />- Extract and summarize technical and commercial requirements from RFQs
              <br />- Compare requirements with previous project submissions and responses
              <br />- Highlight overlaps, coverage, and new or missing requirements for bid teams
            </p>
            <p>Tell me what you are looking for, and I'll help you!</p>
            <strong>Example Prompts:</strong>
            <ul>
              <li>Can you tell me the requirements related to &lt;topic&gt;?</li>
              <li>Can you list down all the requirements related to &lt;topic&gt;?</li>
              <li>
                Can you tell me the requirements related to &lt;topic&gt; and compare it with the
                history submissions?
              </li>
              <li>
                Can you list down all the requirements related to &lt;topic&gt; and compare it with the
                previous responses?
              </li>
            </ul>
          </div>
        ) : (
          <>
            <div className="chat-history">
              {responses.map((res, idx) => (
                <div key={idx} className="chat-response">
                  <strong>You:</strong> {res.query}
                  <br />
                  <strong>AI:</strong> {res.response}
                </div>
              ))}
            </div>

            <div className="chat-input">
              <input
                type="text"
                value={query}
                onChange={(e) => setQuery(e.target.value)}
                placeholder="Ask a question..."
              />
              <button onClick={handleQuery}>Send</button>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default ChatInterface;
