import React, { useState } from "react";
import logo from "../assets/logo.jpg";
import "./MainApp.css"; // Bluewater theme
import "./ChatInterface.css"; // Chat-specific styling

const ChatInterface = () => {
  const [toc, setToc] = useState([]);
  const [sessionId, setSessionId] = useState(null);
  const [loading, setLoading] = useState(false);
  const [messages, setMessages] = useState([]);
  const [userInput, setUserInput] = useState("");

  const handleUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const formData = new FormData();
    formData.append("file", file);
    setLoading(true);
    setMessages([]);
    setToc([]);
    setSessionId(null);

    try {
      const response = await fetch("http://localhost:8000/upload", {
        method: "POST",
        body: formData,
      });

      const data = await response.json();
      setSessionId(data.session_id);
      setToc(data.table_of_contents || []);
    } catch (error) {
      console.error("Upload failed:", error);
    }

    setLoading(false);
  };

  const handleSend = async () => {
    if (!userInput.trim()) return;

    const userMessage = { sender: "user", text: userInput };
    setMessages((prev) => [...prev, userMessage]);
    setUserInput("");
    setLoading(true);

    try {
      const response = await fetch("http://localhost:8000/query", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          session_id: sessionId,
          query: userInput,
        }),
      });

      const data = await response.json();
      const botMessage = { sender: "bot", text: data.answer || "No answer received." };
      setMessages((prev) => [...prev, botMessage]);
    } catch (error) {
      console.error("Query failed:", error);
      setMessages((prev) => [
        ...prev,
        { sender: "bot", text: "Something went wrong. Please try again." },
      ]);
    }

    setLoading(false);
  };

  const handleKeyDown = (e) => {
    if (e.key === "Enter") handleSend();
  };

  return (
    <div className="app-container">
      <aside className="sidebar">
        <img src={logo} alt="Logo" className="logo" />
        <input
          type="file"
          accept="application/pdf"
          onChange={handleUpload}
          className="upload-button"
        />
        {loading && <p className="loading">📄 Extracting and indexing document, please wait...</p>}
        {!loading && toc.length > 0 && (
          <div className="toc">
            <h3>Table of Contents</h3>
            <ul>
              {toc.map((item, index) => (
                <li key={index}>{item.title}</li>
              ))}
            </ul>
          </div>
        )}
      </aside>

      <main className="main-content">
        {!sessionId && (
          <div className="welcome-message">
            <h2>👋 Welcome! I'm your AI-powered RFQ Assistant.</h2>
            <p>
              I can help you to:
              <ul>
                <li>Extract and summarize technical and commercial requirements from RFQs</li>
                <li>Compare requirements with previous project submissions and responses</li>
                <li>Highlight overlaps, coverage, and new or missing requirements for bid teams</li>
              </ul>
              Tell me what you are looking for, and I'll help you!
            </p>
            <p>
              <strong>Example Prompts:</strong>
              <ol>
                <li>Can you tell me the requirements related to &lt;topic&gt;?</li>
                <li>Can you list down all the requirements related to &lt;topic&gt;?</li>
                <li>Compare &lt;topic&gt; requirements with historical submissions.</li>
                <li>Highlight new or missing requirements for &lt;topic&gt;.</li>
              </ol>
            </p>
          </div>
        )}

        {sessionId && (
          <div className="chat-box">
            <div className="chat-history">
              {messages.map((msg, idx) => (
                <div key={idx} className={`chat-message ${msg.sender}`}>
                  <p>{msg.text}</p>
                </div>
              ))}
              {loading && <p className="bot">🤖 Thinking...</p>}
            </div>
            <div className="chat-input-area">
              <input
                type="text"
                value={userInput}
                onChange={(e) => setUserInput(e.target.value)}
                onKeyDown={handleKeyDown}
                placeholder="Ask me something..."
                className="chat-input"
              />
              <button onClick={handleSend} className="send-button">
                Send
              </button>
            </div>
          </div>
        )}
      </main>
    </div>
  );
};

export default ChatInterface;
