import React, { useState } from "react";
import ChatInterface from "./ChatInterface";
import logo from "../assets/logo.jpg";
import "./MainApp.css"; // Make sure this file contains the updated CSS based on Bluewater theme

const UploadAndChatPage = () => {
  const [toc, setToc] = useState([]);
  const [sessionId, setSessionId] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const formData = new FormData();
    formData.append("file", file);

    setLoading(true);
    try {
      const response = await fetch("http://localhost:8000/upload", {
        method: "POST",
        body: formData,
      });
      const data = await response.json();
      setSessionId(data.session_id);
      setToc(data.table_of_contents || []);
    } catch (err) {
      console.error("Upload failed", err);
    }
    setLoading(false);
  };

  return (
    <div className="app-container">
      <aside className="sidebar">
        <img src={logo} alt="Logo" className="logo" />
        <input
          type="file"
          accept="application/pdf"
          onChange={handleUpload}
          className="upload-button"
        />
        {loading && <p className="loading">ðŸ“„ Extracting and indexing document, please wait...</p>}
        {!loading && toc.length > 0 && (
          <div className="toc">
            <h3>Table of Contents</h3>
            <ul>
              {toc.map((item, index) => (
                <details key={index}>
                  <summary>{item.title}</summary>
                  {item.subtitles && (
                    <ul>
                      {item.subtitles.map((sub, i) => (
                        <li key={i}>{sub}</li>
                      ))}
                    </ul>
                  )}
                </details>
              ))}
            </ul>
          </div>
        )}
      </aside>

      <main className="main-content">
        {!sessionId && (
          <div className="welcome-message">
            <h2>ðŸ‘‹ Welcome! I'm your AI-powered RFQ Assistant.</h2>
            <p>
              I can help you to:
              <ul>
                <li>
                  Extract and summarize technical and commercial requirements from RFQs
                </li>
                <li>
                  Compare requirements with previous project submissions and responses
                </li>
                <li>
                  Highlight overlaps, coverage, and new or missing requirements for bid teams
                </li>
              </ul>
              Tell me what you are looking for, and I'll help you!
            </p>
            <p>
              <strong>Example Prompts:</strong>
              <ol>
                <li>Can you tell me the requirements related to &lt;topic&gt;?</li>
                <li>Can you list down all the requirements related to &lt;topic&gt;?</li>
                <li>
                  Can you tell me the requirements related to &lt;topic&gt; and compare it with the
                  history submissions?
                </li>
                <li>
                  Can you list down all the requirements related to &lt;topic&gt; and compare it with the
                  previous responses?
                </li>
              </ol>
            </p>
          </div>
        )}
        {sessionId && <ChatInterface sessionId={sessionId} />}
      </main>
    </div>
  );
};

export default UploadAndChatPage;
