export function intelligentFormatToHTML(text) {
    const lines = text.trim().split('\n');
    let html = '';
    let inList = false;

    for (let line of lines) {
        const trimmed = line.trim();

        const sectionMatch = trimmed.match(/^([A-Z][A-Za-z ]+):$/);
        if (sectionMatch) {
            if (inList) {
                html += '</ul>';
                inList = false;
            }
            html += `<h2>${sectionMatch[1]}</h2>`;
            continue;
        }

        if (/^\d+\.\s+/.test(trimmed)) {
            if (!inList) {
                html += '<ol>';
                inList = true;
            }
            html += `<li>${trimmed.replace(/^\d+\.\s+/, '')}</li>`;
            continue;
        }

        if (/^- /.test(trimmed)) {
            if (!inList) {
                html += '<ul>';
                inList = true;
            }
            html += `<li>${trimmed.replace(/^- /, '')}</li>`;
            continue;
        }

        if (trimmed === '') {
            if (inList) {
                html += '</ul>';
                inList = false;
            }
            html += '<br />';
            continue;
        }

        if (inList) {
            html += '</ul>';
            inList = false;
        }
        //console.log(${ trimmed })
        html += `<p>${trimmed}</p>`;
    }

    if (inList) html += '</ul>';
    return html;
}
