import React, { useState } from "react";
import "./ChatInterface.css"; // use for layout styling

const ChatInterface = () => {
  const [file, setFile] = useState(null);
  const [toc, setToc] = useState(null);
  const [sessionId, setSessionId] = useState(null);
  const [query, setQuery] = useState("");
  const [chatHistory, setChatHistory] = useState([]);

  const handleFileChange = (e) => setFile(e.target.files[0]);

  const handleUpload = async () => {
    if (!file) return;
    const formData = new FormData();
    formData.append("file", file);

    const res = await fetch("http://localhost:8000/upload", {
      method: "POST",
      body: formData,
    });

    const data = await res.json();
    setToc(data.table_of_contents);
    setSessionId(data.session_id);
    setChatHistory([]); // clear history on new upload
  };

  const handleAsk = async () => {
    if (!query.trim()) return;

    const formData = new FormData();
    formData.append("session_id", sessionId);
    formData.append("query", query);

    const res = await fetch("http://localhost:8000/query", {
      method: "POST",
      body: formData,
    });

    const data = await res.json();

    setChatHistory((prev) => [...prev, { q: query, a: data.response }]);
    setQuery("");
  };

  return (
    <div className="container">
      <div className="sidebar">
        <h3>Upload PDF</h3>
        <input type="file" onChange={handleFileChange} />
        <button onClick={handleUpload}>Upload</button>
      </div>

      <div className="main">
        {!sessionId ? (
          <div className="instructions">
            <h2>👋 Welcome! I'm your AI-powered RFQ Assistant.</h2>
            <p>
              I can help you to:

              - Extract and summarize technical and commercial requirements from RFQs\n
              - Compare requirements with previous project submissions and responses\n
              - Highlight overlaps, coverage, and new or missing requirements for bid teams\n\

              Tell me what you are looking for, and I'll help you!".
            </p>
          </div>
        ) : (
          <>
            <div className="toc">
              <h3>📄 Table of Contents</h3>
              <ul>
                {toc.map((item, index) => (
                  <li key={index}>{item}</li>
                ))}
              </ul>
            </div>

            <div className="chatbox">
              <div className="chat-history">
                {chatHistory.map((entry, i) => (
                  <div key={i}>
                    <strong>You:</strong> {entry.q}
                    <br />
                    <strong>Bot:</strong> {entry.a}
                    <hr />
                  </div>
                ))}
              </div>

              <div className="chat-input">
                <input
                  type="text"
                  value={query}
                  onChange={(e) => setQuery(e.target.value)}
                  placeholder="Ask a question..."
                />
                <button onClick={handleAsk}>Send</button>
              </div>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default ChatInterface;
